  `thumb` varchar(191) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '文章缩略图',
  `content` text COLLATE utf8mb4_unicode_ci COMMENT '文章正文',
  `editor` varchar(191) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '作者',
  `view` int(11) DEFAULT '0' COMMENT '查看次数',
  `cate_id` int(11) DEFAULT '0' COMMENT '标题',
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL,
  `deleted_at` timestamp NULL DEFAULT NULL
) ENGINE=MyISAM DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

